body.script:
//
const API_BASE = 'https://ipllive.site/AuraTrendingMovies/DualEngineAPI.php'; 
const IMG_BASE = 'https://image.tmdb.org/t/p/w500';
let currentPage = 1; let currentType = 'movie'; let activeId = null; let activeSeason = 1; let currentTrailerKey = null; let activeServerUrl = null; 

document.addEventListener('DOMContentLoaded', () => { loadHome(); setupSearch(); });
function loadHome() { document.getElementById('sectionTitle').innerText = 'Trending Now'; fetchContent('discover'); }
function filterType(type) { currentType = type; currentPage = 1; document.getElementById('contentGrid').innerHTML = ''; document.getElementById('sectionTitle').innerText = type === 'movie' ? 'Popular Movies' : 'Trending Web Series'; fetchContent('discover'); }
async function fetchContent(action, query = '') {
    const loader = document.getElementById('mainLoader'); loader.classList.remove('hidden');
    let url = `${API_BASE}?action=${action}&page=${currentPage}&type=${currentType}`; if(query) url += `&query=${query}`;
    try { const res = await fetch(url); const data = await res.json(); if (data.results) renderGrid(data.results); } catch (e) { console.error('Error:', e); } finally { loader.classList.add('hidden'); }
}
function renderGrid(items) {
    const grid = document.getElementById('contentGrid');
    items.forEach(item => {
        if(!item.poster_path) return;
        const title = item.title || item.name; const date = item.release_date || item.first_air_date || 'N/A'; const rating = item.vote_average ? item.vote_average.toFixed(1) : 'N/A';
        const card = document.createElement('div'); card.className = 'movie-card bg-[#15171e] rounded-xl overflow-hidden cursor-pointer relative group border border-gray-800'; card.onclick = () => openModal(item.id, currentType);
        card.innerHTML = `<div class="relative aspect-[2/3] overflow-hidden"><img src="${IMG_BASE}${item.poster_path}" class="w-full h-full object-cover group-hover:scale-110 transition duration-500" loading="lazy"><div class="absolute inset-0 bg-black/60 flex items-center justify-center play-overlay"><i class="fas fa-play-circle text-5xl text-neon drop-shadow-lg"></i></div><div class="absolute top-2 right-2 bg-neon text-black text-xs font-bold px-2 py-1 rounded">${rating}</div></div><div class="p-3"><h3 class="text-white font-bold truncate text-sm">${title}</h3><p class="text-gray-500 text-xs mt-1">${date.split('-')[0]}</p></div>`;
        grid.appendChild(card);
    });
}
function setupSearch() {
    const input = document.getElementById('searchInput'); let timeout;
    input.addEventListener('input', (e) => { clearTimeout(timeout); timeout = setTimeout(() => { const q = e.target.value; if(q.length > 2) { document.getElementById('contentGrid').innerHTML = ''; document.getElementById('sectionTitle').innerText = `Search: ${q}`; fetchContent('search', q); } }, 500); });
}
async function openModal(id, type) {
    activeId = id; currentType = type;
    document.getElementById('fakePlayer').classList.remove('hidden'); document.getElementById('videoFrame').classList.add('hidden'); document.getElementById('videoFrame').src = ''; document.getElementById('trailerFrame').classList.add('hidden'); document.getElementById('timerOverlay').classList.add('hidden'); document.getElementById('serverList').innerHTML = ''; document.getElementById('tvSelectors').classList.add('hidden');
    const modal = document.getElementById('playerModal'); modal.classList.remove('hidden'); document.body.style.overflow = 'hidden'; 
    const res = await fetch(`${API_BASE}?action=details&id=${id}&type=${type}`); const data = await res.json();
    document.getElementById('mPoster').src = `${IMG_BASE}${data.poster_path}`; document.getElementById('mTitle').innerText = data.title || data.name; document.getElementById('mRating').innerText = `IMDB ${data.vote_average.toFixed(1)}`; document.getElementById('mYear').innerText = (data.release_date || data.first_air_date || '').split('-')[0]; document.getElementById('mPlot').innerText = data.overview ? data.overview : 'No overview available.';
    currentTrailerKey = null; if(data.videos && data.videos.results) { const trailer = data.videos.results.find(v => v.type === 'Trailer' && v.site === 'YouTube'); if(trailer) currentTrailerKey = trailer.key; } document.getElementById('trailerBtn').style.display = currentTrailerKey ? 'flex' : 'none';
    if (type === 'tv') { document.getElementById('tvSelectors').classList.remove('hidden'); loadSeasons(data.number_of_seasons); } else { loadServers(id, type); }
}
function closeModal() { document.getElementById('playerModal').classList.add('hidden'); document.getElementById('videoFrame').src = ''; document.body.style.overflow = 'auto'; }
function playTrailer() { if(!currentTrailerKey) return; const frame = document.getElementById('trailerFrame'); document.getElementById('fakePlayer').classList.add('hidden'); document.getElementById('videoFrame').classList.add('hidden'); document.getElementById('timerOverlay').classList.add('hidden'); frame.classList.remove('hidden'); frame.src = `https://www.youtube.com/embed/${currentTrailerKey}?autoplay=1`; }
function loadSeasons(count) { const select = document.getElementById('seasonSelect'); select.innerHTML = '<option>Select Season</option>'; for(let i=1; i<=count; i++) { select.innerHTML += `<option value="${i}">Season ${i}</option>`; } select.value = 1; loadEpisodes(1); }
async function loadEpisodes(season) { activeSeason = season; const res = await fetch(`${API_BASE}?action=season_details&id=${activeId}&s=${season}`); const data = await res.json(); const select = document.getElementById('episodeSelect'); select.innerHTML = '<option>Select Episode</option>'; if(data.episodes) { data.episodes.forEach(ep => { select.innerHTML += `<option value="${ep.episode_number}">Ep ${ep.episode_number}: ${ep.name}</option>`; }); } loadServers(activeId, 'tv', season, 1); }
function selectEpisode(ep) { loadServers(activeId, 'tv', activeSeason, ep); document.getElementById('fakePlayer').classList.remove('hidden'); document.getElementById('videoFrame').classList.add('hidden'); document.getElementById('videoFrame').src = ''; document.getElementById('timerOverlay').classList.add('hidden'); }
async function loadServers(id, type, s=1, e=1) {
    let url = `${API_BASE}?action=stream_sources&id=${id}&type=${type}`; if(type === 'tv') url += `&s=${s}&e=${e}`;
    const res = await fetch(url); const data = await res.json(); const list = document.getElementById('serverList'); list.innerHTML = '';
    if(data.servers) { activeServerUrl = data.servers[0].data; data.servers.forEach((server, index) => { const btn = document.createElement('div'); btn.className = `server-btn bg-[#1a1c24] p-3 rounded cursor-pointer flex flex-col items-center justify-center gap-1 ${index === 0 ? 'active' : ''}`; btn.innerHTML = `<span class="text-xl">${server.icon}</span><span class="text-xs font-bold text-gray-300">${server.label}</span>`; btn.dataset.url = server.data; btn.onclick = () => { document.querySelectorAll('.server-btn').forEach(b => b.classList.remove('active')); btn.classList.add('active'); activeServerUrl = server.data; triggerMoneyLoop(); }; list.appendChild(btn); }); }
}
function triggerMoneyLoop() {
    document.getElementById('fakePlayer').classList.add('hidden'); document.getElementById('trailerFrame').classList.add('hidden');
    const timerOverlay = document.getElementById('timerOverlay'); timerOverlay.classList.remove('hidden');
    const timerCount = document.getElementById('timerCount'); let timeLeft = 15; timerCount.innerText = timeLeft;
    const countdown = setInterval(() => { timeLeft--; timerCount.innerText = timeLeft; if (timeLeft <= 0) { clearInterval(countdown); revealPlayer(); } }, 1000);
}
function revealPlayer() {
    document.getElementById('timerOverlay').classList.add('hidden'); const frame = document.getElementById('videoFrame');
    if(activeServerUrl) { const decodedUrl = atob(activeServerUrl); if (decodedUrl.includes('godriveplayer.com')) { frame.referrerPolicy = "no-referrer"; } else { frame.referrerPolicy = "origin"; } frame.src = decodedUrl; frame.classList.remove('hidden'); }
}
function loadMore() { currentPage++; fetchContent('discover'); }
